import React, { useEffect, useState } from "react";
import { View, Text, StyleSheet, Image, TouchableOpacity, Linking } from "react-native";
import moment from "moment"; // Import moment library
import { services } from "./config/service";
import { NativeBaseProvider, FlatList, ScrollView } from 'native-base';
const News = () => {
  const [newsData, setNewsData] = useState([])
  useEffect(() => {
      services('general')
          .then(data => {
              setNewsData(data)
          })
          .catch(error => {
              alert(error)
          })
  }, [])

  const openNewsLink = (url) => {
    Linking.openURL(url).catch((err) => console.error("An error occurred", err));
  };
  const renderNewsItem = ({ item }) => (
    <TouchableOpacity onPress={() => openNewsLink(item.url)}>
      <View style={styles.newsContainer}>
        <Image
          width={355}
          height={200}
          resizeMode={"cover"}
          source={{
            uri: item.urlToImage,
          }}
          alt="Alternate Text"
        />

        <Text style={styles.title}>{item.title}</Text>
        <Text style={styles.date}>{moment(item.publishedAt).format("LLL")}</Text>
        <Text style={styles.newsDescription}>{item.description}</Text>
      </View>
    </TouchableOpacity>
  );
  return (
    <NativeBaseProvider>
      <FlatList
        data={newsData}
        renderItem={renderNewsItem}
        keyExtractor={(item, index) => (item.id || index).toString()}
      />
    </NativeBaseProvider>
  );
};

const styles = StyleSheet.create({
  newsContainer: {
    padding: 10,
  },
  title: {
    fontSize: 18,
    marginTop: 10,
    fontWeight: "600",
  },
  newsDescription: {
    fontSize: 16,
    marginTop: 10,
  },
  date: {
    fontSize: 14,
  },
});

export default News;
